                return <img className="media" src={props.media} />;
                break;
            case "2":
            return <video className="media" src={props.media} /> ;
                break;
            case "3":
                return <audio className="media" src={props.media} />;
                default:
                return null;
            }
        }
        )()}
            </div>

    );
}
export default MediaClip;

